    private $url = "http://api.openweathermap.org/data/2.5/weather?";
    private $dwd_region = "Main-Taunus-Kreis";
    private $dwd_datetime_pattern = "d.m. H:i";
    private $included_types = array(0, 1, 2, 3, 6, 8);

    public function get_weather_warning($load_prewarnings = FALSE) {
        $json_object = $this->get_dwd_json_as_object();
        if($json_object == NULL){
            return NULL;
        }
        $warnings = $json_object->warnings;
        $hasWarning = false;
        $prewarnings = $json_object->vorabInformation;

        //echo "\n Warnstufe............: ".$value[$j]->level;
        // 5 = Warnungen vor extremem Unwetter
        // 4 = Unwetterwarnungen
        // 3 = Warnungen vor markantem Wetter
        // 2 = Wetterwarnungen
        // 1 = Vorabinformatio Unwetter
        // echo "\n Warntyp..............: ".$value[$j]->type;
        // In folgender Tabelle sind Warntypen mit ? markiert, die noch nicht in der Praxis getestet werden konnten!
        // 0 = Gewitter inklusive Begleiterscheinungen ?
        // 1 = Wind/Sturm/Orkan
        // 2 = Stark- und Dauerregen
        // 3 = Schneefall/Schneeverwehungen
        // 4 = Nebel ?
        // 5 = Frost
        // 6 = Glätte/Glatteis
        // 7 = Tauwetter ?
        // 8 = Hitzewarnungen ?
        // 9 = UV-Warnungen ?
        //10 = Küstenwarnungen ?
        //11 = Binnenseewarnungen ?

        foreach ($warnings as $warning) {
            $content = $warning[0];
            $content->regionName = $this->trans_toUmlaut($content->regionName);

            if ($content->regionName == $this->dwd_region && in_array($content->type, $this->included_types)) {
                $hasWarning = true;
                $content->description = $this->trans_toUmlaut($content->description);
                $content->headline = $this->trans_toUmlaut($content->headline);
                $content->event = $this->trans_toUmlaut($content->event);
                $content->instruction = $this->trans_toUmlaut($content->instruction);
                $content->start = date($this->dwd_datetime_pattern, substr($content->start, 0, -3));
                $content->end = date($this->dwd_datetime_pattern, substr($content->end, 0, -3));
                return $content;
            } else {
                continue;
            }
        }

        if (!$hasWarning && $load_prewarnings) {
            foreach ($prewarnings as $prewarning) {
                $content = $prewarning[0];
                $content->regionName = $this->trans_toUmlaut($content->regionName);

            if ($content->regionName == $this->dwd_region && in_array($content->type, $this->included_types)) {
                    $content->description = $this->trans_toUmlaut($content->description);
                    $content->headline = $this->trans_toUmlaut($content->headline);
                    $content->event = $this->trans_toUmlaut($content->event);
                    $content->instruction = $this->trans_toUmlaut($content->instruction);
                    $content->start = date($this->dwd_datetime_pattern, substr($content->start, 0, -3));
                    $content->end = date($this->dwd_datetime_pattern, substr($content->end, 0, -3));
                    return $content;
                }
            }
        }

        return NULL;
    }

    private function get_dwd_json_as_object() {

        if (ENVIRONMENT == 'production') {
            $url = 'https://www.dwd.de/DWD/warnungen/warnapp/json/warnings.json';

            ob_start();
            $out = fopen('php://output', 'w');

            $ch = curl_init();
            curl_setopt($ch, CURLOPT_VERBOSE, true); 
            curl_setopt($ch, CURLOPT_STDERR, $out);  
            curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            curl_setopt($ch, CURLOPT_URL, $url);
            $result = curl_exec($ch);
            
            fclose($out);
            $debug = ob_get_clean();
            
            curl_close($ch);
            
            //var_dump($debug); die();
        } else {
            $result = $this->get_dwd_testjson();
        }

        // JavaScript Function Code entfernen (wird vom DWD als JSONP ausgeliefert)
        $json = $this->extract_unit($result, 'warnWetter.loadWarnings(', ');');

        // Umlaute und ß umwandeln und mit nachfolgendem Q markieren für Rückumwandlung ( wegen json_Decode )
        $json_trans = $this->trans_Umlaut($json);

        return json_decode($json_trans);
    }

    // *** JSON Daten aus JSON Stream extrahieren ***
    private function extract_unit($string, $start, $end) {
        $pos = stripos($string, $start);
        $str = substr($string, $pos);
        $str_two = substr($str, strlen($start));
        $second_pos = stripos($str_two, $end);
        $str_three = substr($str_two, 0, $second_pos);
        $unit = trim($str_three); // remove whitespaces
        return $unit;
    }

    // *** Umlaute als Sonderzeichen entfernen
    private function trans_Umlaut($unit) {
        $trans = array(
            'Ä' => 'AeQ',
            'Ö' => 'OeQ',
            'Ü' => 'UeQ',
            'ä' => 'aeQ',
            'ö' => 'oeQ',
            'ü' => 'ueQ',
            'ß' => 'ssQ',
            //**
            'Ã„' => 'AeQ',
            'Ã–' => 'OeQ',
            'Ãœ' => 'UeQ',
            'Ã¤' => 'aeQ',
            'Ã¶' => 'oeQ',
            'Ã¼' => 'ueQ',
            'ÃŸ' => 'ssQ',
        );
        $data = strtr($unit, $trans);
        return $data;
    }

    // Textformatierung
    private function trans_toUmlaut($unit) {
        $trans = array(
            'AeQ' => 'Ä',
            'OeQ' => 'Ö',
            'UeQ' => 'Ü',
            'aeQ' => 'ä',
            'oeQ' => 'ö',
            'ueQ' => 'ü',
            'ssQ' => 'ß',
            'mÂ²' => 'm²',
            'Â°C' => '°C',
        );
        $data = strtr($unit, $trans);
        return $data;
    }

    private function is_reload($date1, $date2) {

        $interval = $date1->diff($date2);

        if ($interval->y > 0 || $interval->m > 0 || $interval->d > 0 || $interval->h > 1)
            return true;

        if ($interval->h == 1 && $interval->i < 50)
            return true;

        if ($interval->i > 10)
            return true;

        return false;
    }

    public function get_weather() {

        $CI = & get_instance();
        $query = $CI->db->get("weather");
        $json = "";
        $now = new DateTime("now");

        if ($query->num_rows() == 0) {
            $insert = true;
            $reload = true;
        } else {
            $row = $query->row();
            $insert = false;
            $last_call = $row->last_call;
            $reload = $this->is_reload(new DateTime($last_call), $now);
        }

        $query_params = array(
            'id' => $this->city_id_bad_soden,
            //'zip' => $this->zip_code_bad_soden,
            'appid' => $this->api_key,
            'lang' => 'de',
            'units' => 'metric');
        $url = $this->url . http_build_query($query_params);

        if ($reload) {
            // call openweather API
            $ch = curl_init($url);
            curl_setopt($ch, CURLOPT_URL, $url);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
            curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 5);
            $json = curl_exec($ch);
            curl_close($ch);
            
            $data = array('last_call' => $now->format("Y-m-d H:i:s"), 'json' => $json);
            if ($insert) {
                $data["id"] = 1;
                $CI->db->insert('weather', $data);
            } else {
                $CI->db->where('id', $row->id);
                $CI->db->update('weather', $data);
            }
        } else {
            $json = $row->json;
        }

        $weather_obj = json_decode($json);
        if($weather_obj != NULL && $weather_obj->cod == "200") {
            $weather["temperature"] = $weather_obj->main->temp;
            $weather["description"] = $weather_obj->weather[0]->description;
            $weather["description"] = str_replace(" ", "<br/>", $weather["description"]);
            $icon = $this->get_icon_mapping($weather_obj->weather[0]->icon);
            if($icon != null) {
                $weather["image"] = $icon;                
            } else {
                $weather["image"] = "";
            }
            return $weather;
        } else return NULL;
    }
    
    private function get_icon_mapping($code) {
        switch($code) {
            case "01d": return "sonne.svg";
            case "02d": return "wolken_sonne.svg";
            case "03d": return "wolken.svg";
            case "04d": return "wolken.svg";
            case "09d": return "regen.svg";
            case "10d": return "regen.svg";
            case "11d": return "gewitter.svg";
            case "13d": return "schnee.svg";
            case "50d": return "nebel.svg";
            case "01n": return "sonne.svg";
            case "02n": return "wolken_sonne.svg";
            case "03n": return "wolken.svg";
            case "04n": return "wolken.svg";
            case "09n": return "regen.svg";
            case "10n": return "regen.svg";
            case "11n": return "gewitter.svg";
            case "13n": return "schnee.svg";
            case "50n": return "nebel.svg"; 
            default: return null;
        }
    }

